import{_ as l,c as i,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const _=JSON.parse('{"title":"清蒸生蚝的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/aquatic/清蒸生蚝.md","filePath":"dishes/aquatic/清蒸生蚝.md"}'),r={name:"dishes/aquatic/清蒸生蚝.md"};function o(s,a,n,h,d,u){return e(),i("div",null,a[0]||(a[0]=[t('<h1 id="清蒸生蚝的做法" tabindex="-1">清蒸生蚝的做法 <a class="header-anchor" href="#清蒸生蚝的做法" aria-label="Permalink to &quot;清蒸生蚝的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>生蚝</li><li>葱</li><li>蒜</li><li>姜</li><li>酱油</li><li>刷子</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>饮用水 1 升</li><li>生蚝 6 个</li><li>葱 3 颗</li><li>蒜 6 瓣</li><li>姜 1 小块</li><li>酱油 每个生蚝 1 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将生蚝用刷子刷干净（没有刷子用牙刷）。</li><li>蒸锅中放水，将蒸屉放上之后，将 6 个生蚝平铺在蒸屉,使用 50%功率，蒸 3 分钟。</li><li>用右手拿着湿抹布掀开烫锅盖，将每个生蚝的外壳掀开一半去掉，生蚝的凸面向下，平面向上，每个放 1 根姜丝,10g 蒜末放到生蚝上。</li><li>关上烫锅盖，100%功率蒸 3.5 分钟。</li><li>停火，用右手拿着抹布掀开烫锅盖，每个放 5ml 酱油。</li><li>盛盘。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',9)]))}const m=l(r,[["render",o]]);export{_ as __pageData,m as default};
