import{_ as i,c as l,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const s="/cook/assets/1.Pos6yP81.jpg",r="/cook/assets/2.jMpziiLS.jpg",o="/cook/assets/3.eL6hIwyg.jpg",q=JSON.parse('{"title":"辣炒花蛤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/aquatic/辣炒花蛤/辣炒花蛤.md","filePath":"dishes/aquatic/辣炒花蛤/辣炒花蛤.md"}'),c={name:"dishes/aquatic/辣炒花蛤/辣炒花蛤.md"};function n(h,a,d,_,u,p){return e(),l("div",null,a[0]||(a[0]=[t('<h1 id="辣炒花蛤的做法" tabindex="-1">辣炒花蛤的做法 <a class="header-anchor" href="#辣炒花蛤的做法" aria-label="Permalink to &quot;辣炒花蛤的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>花蛤</li><li>食盐</li><li>食用油</li><li>姜、蒜、小米辣</li><li>小葱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>花蛤 1500 g</li><li>姜 5 片（长的）</li><li>蒜 1 团</li><li>小米辣 2 个</li><li>食用油 5 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>花蛤加清水加盐浸泡 30 分钟，清洗将沙子吐干净</li><li>姜、蒜、小米辣用器具切碎（越碎越好）</li><li>冷水起锅，花蛤焯水，水开后撇掉浮沫，用温水冲洗干净</li><li>锅中倒入食用油，油温要够热时，放入一半的姜、蒜、小米辣爆香，蒜白颜色转黄时放入花蛤翻炒</li><li>花蛤全部开口后，放入另一半姜、蒜、小米辣，加盐，翻炒</li><li>加入适量热水，翻炒，留作汤汁</li><li>大火收汁，关火，撒上葱花</li></ul><p><img src="'+s+'" alt="示例菜成品"><img src="'+r+'" alt="示例菜成品"><img src="'+o+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p><h2 id="自己做的" tabindex="-1">自己做的 <a class="header-anchor" href="#自己做的" aria-label="Permalink to &quot;自己做的&quot;">​</a></h2>',12)]))}const f=i(c,[["render",n]]);export{q as __pageData,f as default};
