import{_ as e,c as l,o as i,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"水煮玉米的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/breakfast/水煮玉米.md","filePath":"dishes/breakfast/水煮玉米.md"}'),r={name:"dishes/breakfast/水煮玉米.md"};function s(o,a,n,h,d,_){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="水煮玉米的做法" tabindex="-1">水煮玉米的做法 <a class="header-anchor" href="#水煮玉米的做法" aria-label="Permalink to &quot;水煮玉米的做法&quot;">​</a></h1><p>大约 15 分钟可以完成制作。</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>新鲜玉米</li><li>放得下玉米的锅</li><li>水</li><li>盐</li><li>糖（可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>一个带皮玉米</li><li>淹过玉米约半节指头的水</li><li>煮玉米的时候，开始和淡盐水，差不多 2 克盐加 50ml 的水</li><li>根据口味选择加或者不加糖（可选）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将新鲜玉米剥去外皮，剩部分玉米皮入锅</li><li>加入淹过玉米约半节指头的水，加盐和糖</li><li>水煮开之后转至小火，加盖继续煮 15-20 分钟，玉米煮久点没事。</li><li>煮熟后沥干水分，冷却后食用。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',10)]))}const f=e(r,[["render",s]]);export{c as __pageData,f as default};
