import{_ as l,c as e,o,ae as a}from"./chunks/framework.3ZWff6mY.js";const t="/cook/assets/%E5%8F%A3%E6%B0%B4%E9%B8%A1_%E6%B2%B9%E6%B3%BC%E8%BE%A3%E5%AD%90.CzWNpyaG.jpg",c="/cook/assets/%E6%B2%B9%E6%B3%BC%E8%BE%A3%E5%AD%90.BKn1DtqY.jpg",p=JSON.parse('{"title":"油泼辣子的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/condiment/油泼辣子/油泼辣子.md","filePath":"dishes/condiment/油泼辣子/油泼辣子.md"}'),d={name:"dishes/condiment/油泼辣子/油泼辣子.md"};function r(s,i,n,u,h,_){return o(),e("div",null,i[0]||(i[0]=[a('<h1 id="油泼辣子的做法" tabindex="-1">油泼辣子的做法 <a class="header-anchor" href="#油泼辣子的做法" aria-label="Permalink to &quot;油泼辣子的做法&quot;">​</a></h1><p><img src="'+t+'" alt="image"><img src="'+c+'" alt="image"></p><p>制作耗时 10 分钟</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>蒜头</li><li>干辣椒面</li><li>盐</li><li>熟白芝麻</li><li>小米椒</li><li>花生油（可用菜籽油替换）</li><li>家庭小陶瓷碗</li><li>家庭铁勺子</li><li>五香粉 （可选）</li><li>草寇（可选）</li><li>小葱 （可选）</li><li>八角</li><li>花椒</li><li>香叶</li><li>白芷</li><li>姜片（可选）</li><li>糖</li><li>白醋</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>蒜头 1 个</li><li>干辣椒面 100 克</li><li>盐 5 克</li><li>熟白芝麻 15 克</li><li>小米椒 1 个</li><li>花生油 150 毫升 （可用菜籽油替换）</li><li>五香粉 10 克（可选）</li><li>草寇 1 个（可选）</li><li>小葱 3-5 根（可选）</li><li>其他配料：八角(1)、花椒（20-50 粒都可，看个人口味）、香叶（2-3 片）、白芷（2-3 片）、姜片（大拇指粗细的姜切片）（可选）</li><li>糖 30 克</li><li>白醋 5 ml（大概就是小铁勺子的量）</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>拿出蒜头掰 2 个<code>小蒜头</code>去皮</li><li>拿出砧板剁碎<code>小蒜头</code>、<code>小米椒</code></li><li>拿出碗倒入<code>花生油</code></li><li>油热放入<code>其他配料</code>和<code>小葱</code>,等到香料变焦，捞出扔掉</li><li>拿出铁锅将碗内的油放入加热 2 分钟（菜籽油烧至冒烟）</li><li>此时是空碗</li><li>往空碗加入<code>干辣椒面</code>、<code>白芝麻</code>、<code>蒜末</code>、<code>小米椒</code>、<code>盐</code>、<code>五香粉</code>、<code>草寇</code>作为&quot;调料&quot;</li><li>关火将油温冷却至 <code>210</code> 摄氏度</li><li>将锅内热油倒入碗内并用勺子搅拌即可（可以在 <code>165</code> 摄氏度时加入同样&quot;调料&quot;的碗最后进行混合进行增辣）</li><li>倒入热油稍微搅拌后放入白醋，此时会重新沸腾。继续进行搅拌，白醋增香。</li><li>油泼辣子冷却到温热放白糖和味精，白糖可以是辣味柔和，不会那么的呛口</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>五香粉、草寇作为&quot;调料&quot;加入，可以增加香味，使油泼辣子更香</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const q=l(d,[["render",r]]);export{p as __pageData,q as default};
