import{_ as e,c as l,o as i,ae as t}from"./chunks/framework.3ZWff6mY.js";const _=JSON.parse('{"title":"油酥的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/condiment/油酥.md","filePath":"dishes/condiment/油酥.md"}'),r={name:"dishes/condiment/油酥.md"};function o(n,a,s,d,h,c){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="油酥的做法" tabindex="-1">油酥的做法 <a class="header-anchor" href="#油酥的做法" aria-label="Permalink to &quot;油酥的做法&quot;">​</a></h1><p>油酥是由面粉与热油混合调制的，通常在烙饼时涂点油酥，可以使得饼子层层分明，外酥里软，口感更佳。</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>面粉</li><li>油</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>油 = （要烙饼的张数 * 10ml）</li><li>盐 = （要烙饼的张数 / 2）g</li><li>面粉 = （要烙饼的张数 / 0.13）g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>面粉盛小碗里，加入盐</li><li>加入 200 度的热油</li><li>用筷子将其搅拌成无固状物体的糊状。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',10)]))}const m=e(r,[["render",o]]);export{_ as __pageData,m as default};
