import{_ as l,c as e,o as i,ae as t}from"./chunks/framework.3ZWff6mY.js";const _=JSON.parse('{"title":"糖醋汁的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/condiment/糖醋汁.md","filePath":"dishes/condiment/糖醋汁.md"}'),r={name:"dishes/condiment/糖醋汁.md"};function o(n,a,s,d,h,c){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="糖醋汁的做法" tabindex="-1">糖醋汁的做法 <a class="header-anchor" href="#糖醋汁的做法" aria-label="Permalink to &quot;糖醋汁的做法&quot;">​</a></h1><p>糖醋汁通常情况下由清水、白糖、白醋等制成，有些人喜欢放一些番茄酱来增添不一样的酸甜味或放一些淀粉来增加菜肴汤汁的粘性和浓度，糖醋汁可用于糖醋鱼、糖醋里脊、糖醋排骨等菜品的制作</p><p>可依据糖醋汁配制的经典比例 1：2：3：4：5 来调制糖醋汁</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>清水</li><li>白糖</li><li>白醋/米醋</li><li>料酒</li><li>生抽</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>清水(50ml)</li><li>生抽(40ml)</li><li>白糖(30g)</li><li>白醋(20ml)</li><li>料酒(10ml)</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>按照比例将各调料在小碗中搅拌均匀</li><li>按不同菜肴的方式处理完毕后，将配制好的糖醋汁倒入锅中</li><li>根据各菜肴的不同，烹制 5-10 分钟</li><li>大火收汁，可增加菜的浓度、香味和光泽</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const m=l(r,[["render",o]]);export{_ as __pageData,m as default};
