import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"可乐鸡翅的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/可乐鸡翅.md","filePath":"dishes/meat_dish/可乐鸡翅.md"}'),r={name:"dishes/meat_dish/可乐鸡翅.md"};function s(o,l,h,d,n,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="可乐鸡翅的做法" tabindex="-1">可乐鸡翅的做法 <a class="header-anchor" href="#可乐鸡翅的做法" aria-label="Permalink to &quot;可乐鸡翅的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鸡翅中</li><li>可乐</li><li>白糖</li><li>生抽</li><li>盐</li><li>生姜</li><li>料酒或啤酒</li><li>小葱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 盘的份量：</p><ul><li>鸡翅 10 ～ 12 只</li><li>可乐 500ml</li><li>白糖 10 克</li><li>生抽 15 克</li><li>老抽 3 克</li><li>盐 2 克</li><li>生姜 2 片</li><li>料酒 20 毫升</li><li>小葱挽成结</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>鸡翅入锅，倒入冷水淹没。放生姜 1 片和料酒 10 ～ 20 毫升。大火煮开（ 大约 2 分钟 ）后，撇去浮沫，沥出水分 <ul><li>这一步针对冰鲜鸡翅，刚买来的鸡翅直接改刀用生抽进行腌制即可。</li></ul></li><li>捞出鸡翅，可用刀将两边各划上两口改刀。生抽约 10 克腌制鸡翅 10 分钟（生抽能完全包裹鸡翅表面入味就行）</li><li>锅重新小火起油，先将剩余姜片爆香，然后下入腌好的鸡翅。将鸡翅煎至金黄翻面（直到两面金黄），用炒菜勺子翻动一下鸡翅，与姜片一起翻炒 4～5 下（目的是防止鸡翅和姜片粘黏）。</li><li>鸡翅金黄，倒入可乐没过鸡翅，开大火将锅中可乐煮沸，然后撇去漂浮的黑色浮沫（包含血水）。此时加入葱结。</li><li>调味：加入食用盐 2 克，白糖 10 克，生抽 3 克调味（可以适当用老抽调底色，3 克）。</li><li>等到葱结变黄，和姜片一起捞出，转中火继续慢煮可乐鸡翅。</li><li>等到可乐呈现挂丝状态，关小火让汁牢牢挂在鸡翅上。出锅，装盘。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>加入生姜爆香的同时能防止鸡翅粘锅。</li><li>最后收汁时勿开过大火，防止味道偏苦。</li><li>本菜品偏甜。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const m=i(r,[["render",s]]);export{c as __pageData,m as default};
