import{_ as i,c as a,o as e,ae as r}from"./chunks/framework.3ZWff6mY.js";const q=JSON.parse('{"title":"小酥肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/小酥肉.md","filePath":"dishes/meat_dish/小酥肉.md"}'),t={name:"dishes/meat_dish/小酥肉.md"};function h(o,l,d,n,s,u){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="小酥肉的做法" tabindex="-1">小酥肉的做法 <a class="header-anchor" href="#小酥肉的做法" aria-label="Permalink to &quot;小酥肉的做法&quot;">​</a></h1><p>预估烹饪难度：★★★</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>去皮猪肉（根据喜好选择肥瘦）</li><li>植物油</li><li>老姜</li><li>小葱</li><li>料酒</li><li>盐</li><li>十三香</li><li>胡椒粉</li><li>味精</li><li>鸡精</li><li>花椒碎</li><li>花椒粒</li><li>生抽</li><li>鸡蛋</li><li>面粉</li><li>红薯淀粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><h3 id="主料" tabindex="-1">主料 <a class="header-anchor" href="#主料" aria-label="Permalink to &quot;主料&quot;">​</a></h3><ul><li>猪肉 500 克</li></ul><h3 id="辅料" tabindex="-1">辅料 <a class="header-anchor" href="#辅料" aria-label="Permalink to &quot;辅料&quot;">​</a></h3><ul><li>鸡蛋 2 个</li><li>面粉 30 克</li><li>红薯淀粉 120 克</li></ul><h3 id="葱姜水" tabindex="-1">葱姜水 <a class="header-anchor" href="#葱姜水" aria-label="Permalink to &quot;葱姜水&quot;">​</a></h3><ul><li>老姜 20 克</li><li>小葱 15 克</li><li>料酒 15 克</li><li>清水 80 克</li></ul><h3 id="腌制调料" tabindex="-1">腌制调料 <a class="header-anchor" href="#腌制调料" aria-label="Permalink to &quot;腌制调料&quot;">​</a></h3><ul><li>盐 4 克</li><li>十三香 2 克</li><li>胡椒粉 3 克</li><li>味精 5 克</li><li>鸡精 5 克</li><li>花椒碎 3 克</li><li>花椒粒 3 克</li><li>生抽 8 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="葱姜水制作" tabindex="-1">葱姜水制作 <a class="header-anchor" href="#葱姜水制作" aria-label="Permalink to &quot;葱姜水制作&quot;">​</a></h3><ul><li>老姜切丝,小葱不用切。</li><li>根据计算公式倒入料酒、清水。</li><li>用手捏揉 5 分钟，使姜葱的味道充分溶解在水中。</li></ul><h3 id="猪肉处理" tabindex="-1">猪肉处理 <a class="header-anchor" href="#猪肉处理" aria-label="Permalink to &quot;猪肉处理&quot;">​</a></h3><ul><li>将猪肉去皮洗净</li><li>切成长度 8~10 厘米，厚度 1.5 厘米的肉条。</li><li>根据上面的计算公式加入盐，十三香、胡椒粉、味精、鸡精、花椒碎、花椒粒、生抽。</li><li>倒入前面制作好的葱姜水</li><li>抓匀并且充分揉制 10 分钟，直至肉吸收所有水分并且变得粘手。</li><li>封上保鲜膜放冷藏室静置 30 分钟。</li></ul><h3 id="猪肉裹粉" tabindex="-1">猪肉裹粉 <a class="header-anchor" href="#猪肉裹粉" aria-label="Permalink to &quot;猪肉裹粉&quot;">​</a></h3><ul><li>将面粉、红薯粉倒入腌制好的肉中，加入鸡蛋清。</li><li>充分揉制 15 分钟。</li></ul><h3 id="初炸定形状" tabindex="-1">初炸定形状 <a class="header-anchor" href="#初炸定形状" aria-label="Permalink to &quot;初炸定形状&quot;">​</a></h3><ul><li>锅中倒入植物油，根据锅大小控制油量，油面高度 3 厘米以上 °</li><li>大火将温加热至 150° 后，转小火保持温度。</li><li>将裹好粉的肉条用筷子夹入油锅中，捋成自己喜欢的形状，炸 3~5 分钟定型。目测颜色微黄，用锅铲翻动感受倒略微有些硬了就可以。具体时间受肉块大小、油温、裹粉程度影响。</li><li>捞出沥油。</li></ul><p>tips: 如果对时间没把握可以稍微多炸一下，否则肉会带一点生味。 没有经验可以一条一条的下锅，防止粘连。</p><h3 id="复炸出成品" tabindex="-1">复炸出成品 <a class="header-anchor" href="#复炸出成品" aria-label="Permalink to &quot;复炸出成品&quot;">​</a></h3><ul><li>将油温升至 180° 放入初炸好的肉条，炸至金黄色即可捞出。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>裹粉一定要吧红薯粉揉散，不要有干的颗粒裹在里面，否则会炸！！！</li><li>如果肉太多吃不完，可以只炸一遍然后放冰箱。后面再吃的时候拿出来复炸，口感基本没影响。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',28)]))}const b=i(t,[["render",h]]);export{q as __pageData,b as default};
