import{_ as a,c as i,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const _=JSON.parse('{"title":"尖椒炒牛肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/尖椒炒牛肉.md","filePath":"dishes/meat_dish/尖椒炒牛肉.md"}'),r={name:"dishes/meat_dish/尖椒炒牛肉.md"};function o(h,l,s,d,n,u){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="尖椒炒牛肉的做法" tabindex="-1">尖椒炒牛肉的做法 <a class="header-anchor" href="#尖椒炒牛肉的做法" aria-label="Permalink to &quot;尖椒炒牛肉的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>牛肉</li><li>葱、姜、蒜</li><li>尖椒</li><li>酱油</li><li>盐</li><li>糖</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>牛肉用量为 250 g/人</li><li>葱的用量为 0.5 根/人</li><li>姜的用量为 1 个/人</li><li>盐的用量为 3 g/人</li><li>糖的用量为 1.5 g/人</li><li>酱油的用量为 9 ml/人</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><h3 id="配菜处理" tabindex="-1">配菜处理 <a class="header-anchor" href="#配菜处理" aria-label="Permalink to &quot;配菜处理&quot;">​</a></h3><ul><li>蒜剁成蒜泥</li><li>葱切段</li><li>姜切成姜片</li><li>尖椒切成段</li></ul><h3 id="牛肉的预处理" tabindex="-1">牛肉的预处理 <a class="header-anchor" href="#牛肉的预处理" aria-label="Permalink to &quot;牛肉的预处理&quot;">​</a></h3><ul><li>牛肉放入碗中</li><li>加姜、盐、酱油、糖进行腌制 30-40 分钟</li><li>腌制完姜可以去掉</li></ul><h3 id="炒熟" tabindex="-1">炒熟 <a class="header-anchor" href="#炒熟" aria-label="Permalink to &quot;炒熟&quot;">​</a></h3><ul><li>冷油下锅，待油变热至偶有气泡</li><li>加入蒜泥</li><li>蒜泥变金黄后加入尖椒</li><li>待尖椒表皮微皱，加入腌制好的牛肉翻炒</li><li>翻炒变熟之前加入葱，继续翻炒</li><li>翻炒至牛肉变熟，关火出锅</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>腌制可以加入小苏打使牛肉变嫩，牛肉苏打比例为 500g：3g。</li><li>腌制的时候姜片可用料酒代替，料酒的用量为 3 g/人</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',16)]))}const m=a(r,[["render",o]]);export{_ as __pageData,m as default};
