import{_ as l,c as s,o as t,ae as a}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/sznr1.BAkxmAeI.jpg",o="/cook/assets/sznr2.rIkpGEM0.jpg",u="/cook/assets/sznr3.DL7f57Rv.jpg",e="/cook/assets/sznr4.D0X6TuHo.jpg",p="/cook/assets/sznr5.DikkUODM.jpg",n="/cook/assets/sznr6.FBlqkXCV.jpg",c="/cook/assets/sznr7.ojV0ltFs.jpg",h="/cook/assets/sznr8.CPyp5A64.jpg",_="/cook/assets/sznr9.D3LvI1ab.jpg",m="/cook/assets/sznr10.CZ8GTgvI.jpg",g="/cook/assets/sznr11.BQNIofDR.jpg",d="/cook/assets/sznr12.CqHD-SCg.jpg",D=JSON.parse('{"title":"水煮牛肉的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/水煮牛肉/水煮牛肉.md","filePath":"dishes/meat_dish/水煮牛肉/水煮牛肉.md"}'),k={name:"dishes/meat_dish/水煮牛肉/水煮牛肉.md"};function z(f,i,q,b,j,P){return t(),s("div",null,i[0]||(i[0]=[a('<h1 id="水煮牛肉的做法" tabindex="-1">水煮牛肉的做法 <a class="header-anchor" href="#水煮牛肉的做法" aria-label="Permalink to &quot;水煮牛肉的做法&quot;">​</a></h1><p><img src="'+r+'" alt="shuizhuniurou"></p><p>麻辣鲜香</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>牛肉</li><li>豆芽</li><li>鸡蛋</li><li>香菜</li><li>豆瓣酱</li><li>料酒</li><li>淀粉</li><li>干辣椒粉</li><li>姜</li><li>蒜</li><li>红辣椒</li><li>蚝油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 2 个人吃。</p><p>这是一份的量：</p><ul><li>牛肉 300g</li><li>豆芽 100g</li><li>鸡蛋 1 个</li><li>香菜 5 根</li><li>豆瓣酱 10g</li><li>料酒 10ml</li><li>淀粉 15g</li><li>干辣椒粉 5g</li><li>姜 20g</li><li>蒜 3 瓣</li><li>红辣椒 1 根</li><li>蚝油 8g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>牛肉洗干净切片。</li></ul><p><img src="'+o+'" alt="shuizhuniurou"></p><ul><li>加入 15g 姜丝，1 个鸡蛋，15g 淀粉，8g 蚝油，10ml 料酒搅拌均匀，腌制 15 分钟。</li></ul><p><img src="'+u+'" alt="shuizhuniurou"></p><ul><li>香菜洗干净切好。</li></ul><p><img src="'+e+'" alt="shuizhuniurou"></p><ul><li>锅里倒油，加入豆瓣酱，5g 姜丝，蒜片。</li></ul><p><img src="'+p+'" alt="shuizhuniurou"></p><ul><li>倒入开水，煮成红汤。</li></ul><p><img src="'+n+'" alt="shuizhuniurou"></p><ul><li>豆芽洗干净去掉尾须，放进开水里焯熟。</li></ul><p><img src="'+c+'" alt="shuizhuniurou"></p><ul><li>将豆芽铺入碗底。</li></ul><p><img src="'+h+'" alt="shuizhuniurou"></p><ul><li>将牛肉片一片一片的放进红汤中，煮熟以后捞出。</li></ul><p><img src="'+_+'" alt="shuizhuniurou"></p><ul><li>将牛肉铺在豆芽上，撒上香菜梗。</li></ul><p><img src="'+m+'" alt="shuizhuniurou"></p><ul><li>撒上香菜叶，辣椒粉，辣椒圈。</li></ul><p><img src="'+g+'" alt="shuizhuniurou"></p><ul><li>另起锅烧热油，将热油淋在菜上面，就完成了。</li></ul><p><img src="'+d+'" alt="shuizhuniurou"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>参考: <a href="https://www.zhms.cn/recipe/blrqm.html?source=2" target="_blank" rel="noreferrer">水煮牛肉的详细步骤</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',35)]))}const I=l(k,[["render",z]]);export{D as __pageData,I as default};
