import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const s="/cook/assets/1.Brl9ux_q.jpeg",r="/cook/assets/2.BJ_S08Ud.jpeg",p=JSON.parse('{"title":"糖醋排骨的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/糖醋排骨/糖醋排骨.md","filePath":"dishes/meat_dish/糖醋排骨/糖醋排骨.md"}'),o={name:"dishes/meat_dish/糖醋排骨/糖醋排骨.md"};function _(d,l,h,n,c,m){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="糖醋排骨的做法" tabindex="-1">糖醋排骨的做法 <a class="header-anchor" href="#糖醋排骨的做法" aria-label="Permalink to &quot;糖醋排骨的做法&quot;">​</a></h1><p>是糖醋味型中具有代表性的一道大众喜爱的特色传统名菜</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>排骨</li><li>白砂糖</li><li>食用油</li><li>生抽</li><li>蚝油</li><li>老抽</li><li>鸡精</li><li>姜片</li><li>芝麻</li><li>番茄酱</li><li>香醋</li><li>五香粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>排骨 300 g</li><li>白砂糖 20 g</li><li>食用油 350 ml</li><li>生抽 5 ml</li><li>蚝油 5 ml</li><li>老抽 5 ml</li><li>鸡精 2 g</li><li>姜片 2 片</li><li>芝麻 2 g</li><li>番茄酱 10 g</li><li>香醋 5 ml</li><li>五香粉 2 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>排骨+姜片冷水下锅，煮开后用勺子舀去白色油沫，2-3 分钟后出锅</li><li>冷水清洗排骨，清洗 2-3 遍</li><li>锅中加入 300 ml 食用油，油开后倒入排骨，煎炸 3-5 分钟后出锅</li><li>小火起锅，加入 50 ml 食用油，加入白砂糖 30 g ，轻轻搅拌到糖水变成黄色</li><li>倒入排骨翻炒 30 S 后，加入香醋、生抽、蚝油、鸡精、番茄酱、五香粉 翻炒 30 S 后，加入清水没过排骨</li><li>中火煮 20 分钟，加入老抽上色，开锅烧汁</li><li>起锅摆盘，散上芝麻</li></ul><p><img src="'+s+'" alt="示例菜成品"><img src="'+r+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>步骤三油炸排骨时，可以根据实际情况选择加入淀粉包裹排骨</li><li>步骤六可以加少量水淀粉，但汤汁的自勾芡已经足够定性排骨</li><li>烧汁过程中，可以根据汤汁酸甜度选择再加 3-5 g 白砂糖、2-5 ml 香醋</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const f=i(o,[["render",_]]);export{p as __pageData,f as default};
