import{_ as i,c as l,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/1.BEX7IGN6.jpg",s="/cook/assets/2.WKDOVcE-.jpg",p=JSON.parse('{"title":"红烧排骨的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/红烧排骨/红烧排骨.md","filePath":"dishes/meat_dish/红烧排骨/红烧排骨.md"}'),o={name:"dishes/meat_dish/红烧排骨/红烧排骨.md"};function h(n,a,d,_,c,u){return e(),l("div",null,a[0]||(a[0]=[t('<h1 id="红烧排骨的做法" tabindex="-1">红烧排骨的做法 <a class="header-anchor" href="#红烧排骨的做法" aria-label="Permalink to &quot;红烧排骨的做法&quot;">​</a></h1><h2 id="必备原料及工具" tabindex="-1">必备原料及工具 <a class="header-anchor" href="#必备原料及工具" aria-label="Permalink to &quot;必备原料及工具&quot;">​</a></h2><ul><li>排骨（小肋排最佳）</li><li>植物油、生抽酱油、老抽酱油、米酒、盐</li><li>姜、小米辣、葱、（可选）</li><li>白砂糖</li><li>桂皮、八角、香叶、白胡椒粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>排骨（1kg）</li><li>盐两勺</li><li>白砂糖 1.5 勺</li><li>姜 6 片、小米辣 2 个</li><li>桂皮两个、八角 2 颗、香叶 2 片</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>准备排骨：排骨切成 5 厘米的小块，排骨用清水浸泡 5 分钟后，冲洗几次除掉血水。焯水，排骨凉水入锅，同时放入 3 片姜和料酒，水开后焯水一分钟，姜排骨捞出，注意用温水（注意温水）清洗干净，控干水份，等待使用</li><li>排骨上色：倒入少量冷油和 1.5 勺冰糖，小火加热不停搅拌，糖敖化后等到有白色小泡泡冒出并变成红棕色即可（不要熬太久产生焦糊的苦味），加入排骨翻炒上色</li><li>排骨爆香：加入姜片、小米辣、桂皮，香叶、白胡椒粉，爆香后加入两勺盐，2 勺米酒盖锅焖一会</li><li>大火烧开：加入生抽，加入开水，末过排骨即可（不能用冷水），水开后若有浮沫漂浮去掉，再加入老抽上色，大火烧 20 分钟 （人在旁边看着，以防糊锅，准备好备用的开水）</li><li>小火炖煮：转小火，观察收汁情况，以防糊锅，锅中剩余 1/3 的汤汁时，可加入少量白糖，再加入盐调味（如果发现盐淡）</li><li>收汁：大火收汁，期间不停翻炒，使得汤汁均匀裹到排骨表面</li><li>加少许葱花点缀，出锅盛盘</li></ul><h2 id="经验总结" tabindex="-1">经验总结 <a class="header-anchor" href="#经验总结" aria-label="Permalink to &quot;经验总结&quot;">​</a></h2><ul><li>炒糖色用小火，大火容易炒糊</li></ul><p><img src="'+r+'" alt="示例菜成品"><img src="'+s+'" alt="示例菜成品"></p>',10)]))}const f=i(o,[["render",h]]);export{p as __pageData,f as default};
