import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const s="/cook/assets/1.B60bkP9j.webp",r="/cook/assets/2.DhucLKeu.webp",o="/cook/assets/3.CTh-8YeU.png",c="/cook/assets/4.tijMb7Ay.webp",f=JSON.parse('{"title":"肉末豆腐的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/肉末豆腐/肉末豆腐.md","filePath":"dishes/meat_dish/肉末豆腐/肉末豆腐.md"}'),_={name:"dishes/meat_dish/肉末豆腐/肉末豆腐.md"};function d(n,l,h,p,u,m){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="肉末豆腐的做法" tabindex="-1">肉末豆腐的做法 <a class="header-anchor" href="#肉末豆腐的做法" aria-label="Permalink to &quot;肉末豆腐的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>肉末</li><li>嫩豆腐或内酯豆腐</li><li>白糖</li><li>生抽</li><li>盐</li><li>生姜</li><li>蒜</li><li>料酒</li><li>小葱</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 盘的份量：</p><ul><li>白糖 10 克</li><li>生抽 15 克</li><li>老抽 3 克</li><li>盐 2 克</li><li>生姜 2 片</li><li>蒜 2 瓣</li><li>料酒 20 毫升</li><li>小葱挽成结</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>1.准备一块嫩豆腐或者内酯豆腐，切成 1.5 厘米左右的小方块，水里加盐，将切好的豆腐放入水中浸泡一会儿，这样能使豆腐不易碎。葱白葱叶分开，葱白切碎，葱叶切成葱花，姜蒜拍扁切成末，锅里放油。肉末下锅，快速将肉末炒散。</li></ul><p><img src="'+s+'" alt=""></p><ul><li><ol start="2"><li>耐心地把肉末炒香，炒干水分，加入葱、姜、蒜爆香，爆香后从锅边淋入一勺料酒，两勺生抽、酱油。快速搅匀，激发出酱香味，加入一碗清水煮开，加半勺老抽调色，搅拌均匀。加入沥干水的豆腐。轻轻的用铲子从锅边往里推冻豆腐，使豆腐入味均匀</li></ol></li></ul><p><img src="'+r+'" alt=""></p><p><img src="'+o+'" alt=""></p><ul><li><ol start="3"><li>千万不要随意翻动豆腐，否则豆腐易碎。味道如果不够的话，补一勺盐，再加少许糖，提鲜胚冻豆腐，使味道均匀。小火煮至豆腐入味，倒入淀粉水，勾一层薄薄的芡。将汤汁收至浓稠，最后撒上葱花即可起锅。肉末豆腐完成。</li></ol></li></ul><p><img src="'+c+'" alt=""></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li><a href="https://www.bilibili.com/video/BV1iF411e7DM/?spm_id_from=333.999.0.0&amp;vd_source=b080c1e3a5d9d4c1b7d7de6c5e93368e" target="_blank" rel="noreferrer">肉末和豆腐超下饭的做法，鲜嫩可口又入味，营养丰富，老少皆宜！</a></li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const k=i(_,[["render",d]]);export{f as __pageData,k as default};
