import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const u=JSON.parse('{"title":"蒜苔炒肉末的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/蒜苔炒肉末.md","filePath":"dishes/meat_dish/蒜苔炒肉末.md"}'),r={name:"dishes/meat_dish/蒜苔炒肉末.md"};function s(o,l,n,h,d,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="蒜苔炒肉末的做法" tabindex="-1">蒜苔炒肉末的做法 <a class="header-anchor" href="#蒜苔炒肉末的做法" aria-label="Permalink to &quot;蒜苔炒肉末的做法&quot;">​</a></h1><p>蒜苔炒肉末是一道简单易做的菜。这是一道北方家常菜，以做法简单、味道上乘的特点，在广大北方人民群众中备受喜爱。</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>五花肉薄片</li><li>蒜苔</li><li>食用盐</li><li>生抽</li><li>食用油</li><li>蒜瓣</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>1 人食用版本</p><ul><li>蒜苔 1 扎（每扎蒜苔约 190g）</li><li>五花肉薄片 4 片（约 20g）</li><li>食用油 10ml</li><li>蒜瓣 2 瓣</li><li>生抽 15ml</li><li>食盐 2g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>蒜苔切成 5cm 小段，备用</li><li>五花肉切成 5mm * 5cm 丝状，备用</li><li>蒜瓣拍碎切成末，备用</li><li>热锅，锅内放入 10ml 食用油。等待 10 秒让油温升高</li><li>放入蒜末，中火翻炒 <strong>10 秒</strong> 将蒜末炒出香味</li><li>放入五花肉和 5ml 生抽，中火翻炒 <strong>30 秒</strong> 将肉炒熟并上色</li><li>将蒜苔放入锅内并加入 10ml 生抽，翻炒 <strong>30 秒</strong></li><li>锅内加入 20g 水，中火翻炒 <strong>5 分钟</strong> 将蒜苔炒至稍稍变软</li><li>最后加入 2g 食盐，中火翻炒 <strong>30 秒</strong>，即可出锅装盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>加入食盐前可尝一下咸淡，自行决定是否增减盐量</li><li>选用五花肉薄片是因为切肉简单且不用腌制即可入味</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const c=i(r,[["render",s]]);export{u as __pageData,c as default};
