import{_ as a,c as i,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/2024.07.21.Ct_PmUw1.jpg",m=JSON.parse('{"title":"西红柿牛腩的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/meat_dish/西红柿牛腩/西红柿牛腩.md","filePath":"dishes/meat_dish/西红柿牛腩/西红柿牛腩.md"}'),s={name:"dishes/meat_dish/西红柿牛腩/西红柿牛腩.md"};function o(h,l,n,d,_,c){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="西红柿牛腩的做法" tabindex="-1">西红柿牛腩的做法 <a class="header-anchor" href="#西红柿牛腩的做法" aria-label="Permalink to &quot;西红柿牛腩的做法&quot;">​</a></h1><p>西红柿牛腩汤汁浓厚酸甜可口，牛肉软绵醇香，搭配米饭绝配。一般初学者需要 90 分钟完成。</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>西红柿 （3 斤牛肉配 4 个西红柿）</li><li>牛腩</li><li>燃气灶（西红柿去皮用）</li><li>高压锅/砂锅/普通铝锅（铁锅）</li><li>2cm 两段葱段、两片姜片，葱花、姜各 10g</li><li>生抽、白胡椒粉，白糖，料/黄酒，八角三小片</li><li>牛腩（挑选肥瘦相间的口感比较好）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>西红柿 3-4 个（每个约 200g）</li><li>牛腩 500g</li><li>食用油 20-30ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>牛肉放入清水中浸泡 60 分钟，去除牛肉中的血水</li><li>牛腩切条、切块成长宽高均 2cm ，冷水下锅，加入姜片和料酒开锅煮制 2 分钟去除血水，捞出冲洗干净（剩下的汤就是原汤，不要倒了，后面有用到）</li><li>另起锅 2L 水烧开（用来处理西红柿和补炖牛肉原汤不够补充），加入 8cm 两段葱段（或小葱扎捆）、两片姜片、八角、料/黄酒 5-10ml，加盐 两斤放一勺盐，放入焯好的牛肉，盖盖炖制（小火炖 50 分钟或高压锅炖牛肉模式），筷子能轻松插透就证明炖好了</li><li>西红柿去皮：西红柿头部滑十字至腰线，加入开水浸泡 3 分钟，热涨冷缩原理起皮后撕下来，切块。越小越好 <ul><li>撕皮小心烫，去皮后的西红柿特别滑，慢切注意安全</li></ul></li><li>起锅烧油，油温 7 成热，放入葱花、姜各 10g，番茄下锅，炒透炒出番茄红色，加入煮好的牛腩和原汤，原汤刚刚没过牛肉即可 (注意一定不要加过多)</li><li>根据个人口味放入盐、糖、生抽、胡椒粉调味盖盖</li><li>开锅后大火继续炒制 3-5 分钟</li><li>待番茄汁呈中等粘稠程度后关火，盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>用火注意安全、用火注意安全、用火注意安全</li><li>用砂锅/铝锅炖肉时，水开后转中小火/小火，使用高压锅见<a href="./../../../tips/learn/高压力锅.html">学习使用压力锅</a></li><li>绝对不用番茄酱和少加佐料，尽可能还原食材的原味</li></ul><h2 id="成品展示" tabindex="-1">成品展示 <a class="header-anchor" href="#成品展示" aria-label="Permalink to &quot;成品展示&quot;">​</a></h2><p><strong>小记录</strong></p><p>本次使用了 1 个大葱， 1.4 kg 牛肉和 7 个中等大小西红柿</p><p>铁锅炖制牛肉 1 小时 20 分钟，盐 2 勺半偏右。</p><img src="'+r+'" width="50%"><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',17)]))}const p=a(s,[["render",o]]);export{m as __pageData,p as default};
