import{_ as e,c as l,o as i,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"桂圆红枣粥的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/soup/桂圆红枣粥.md","filePath":"dishes/soup/桂圆红枣粥.md"}'),r={name:"dishes/soup/桂圆红枣粥.md"};function o(s,a,n,h,d,u){return i(),l("div",null,a[0]||(a[0]=[t('<h1 id="桂圆红枣粥的做法" tabindex="-1">桂圆红枣粥的做法 <a class="header-anchor" href="#桂圆红枣粥的做法" aria-label="Permalink to &quot;桂圆红枣粥的做法&quot;">​</a></h1><p>桂圆红枣粥，甜口。补血安神，健脑益智，补养心脾。制作时间需要 70 分钟。</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>糯米（或大米）</li><li>红枣</li><li>桂圆</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>糯米 100g</li><li>红枣 15 颗</li><li>桂圆 15 颗</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将桂圆肉扒出，用清水洗两次，放入碗中浸泡 10 分钟</li><li>红枣用清水洗两次，放入碗中浸泡 10 分钟</li><li>糯米放入电饭锅中，清水淘米两次后，加入 2000ml 水</li><li>将桂圆和红枣加入电饭锅</li><li>打开电饭锅煮饭模式，1 小时后粥成</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>对粥的稀稠程度有不同喜好的朋友可以酌情增加或减少水的用量，煮出来的粥是甜的，可以通过控制加入桂圆的数量控制甜度</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const p=e(r,[["render",o]]);export{c as __pageData,p as default};
