import{_ as a,c as i,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const o="/cook/assets/%E6%8B%8C%E7%96%99%E7%98%A9.DlQpWLUx.jpg",s="/cook/assets/%E7%96%99%E7%98%A9%E6%B1%A4.BapHR-ue.jpg",m=JSON.parse('{"title":"疙瘩汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/soup/疙瘩汤/疙瘩汤.md","filePath":"dishes/soup/疙瘩汤/疙瘩汤.md"}'),r={name:"dishes/soup/疙瘩汤/疙瘩汤.md"};function n(h,l,p,c,d,_){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="疙瘩汤的做法" tabindex="-1">疙瘩汤的做法 <a class="header-anchor" href="#疙瘩汤的做法" aria-label="Permalink to &quot;疙瘩汤的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>西红柿</li><li>鸡蛋</li><li>香油</li><li>盐</li><li>葱、姜、蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><ul><li>西红柿 1 个</li><li>鸡蛋 1-2 个（依照自己的口味而定，一般就放 2 个）</li><li>香油 2 滴</li><li>盐 15 克</li><li>葱、姜、蒜共 15 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ol><li>西红柿切小丁，尽量切得小一些</li><li>面粉里缓缓的滴入水滴，将面粉搅拌成絮状</li></ol><p><img src="'+o+'" alt=""></p><p>(注意疙瘩不能拌的太大了，否则会煮不熟)</p><ol start="3"><li>锅中倒入少许食用油，放入西红柿翻炒，少加盐（只给西红柿）。</li><li>翻炒至西红柿出汁</li><li>加入适量水烧开</li><li>将面粉缓缓倒入锅中</li><li>再次煮开</li><li>鸡蛋打散，淋入锅中</li><li>撒少许盐调味</li><li>撒入葱花即可出锅</li></ol><p>成品展示</p><p><img src="'+s+'" alt=""></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',14)]))}const f=a(r,[["render",n]]);export{m as __pageData,f as default};
