import{_ as a,c as i,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"紫菜蛋花汤的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/soup/紫菜蛋花汤.md","filePath":"dishes/soup/紫菜蛋花汤.md"}'),r={name:"dishes/soup/紫菜蛋花汤.md"};function o(s,l,n,h,d,u){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="紫菜蛋花汤的做法" tabindex="-1">紫菜蛋花汤的做法 <a class="header-anchor" href="#紫菜蛋花汤的做法" aria-label="Permalink to &quot;紫菜蛋花汤的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>鸡蛋</li><li>紫菜</li><li>葱花</li><li>水</li><li>盐</li><li>油</li><li>虾仁（个人口味，可加可不加）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 人份的份量：</p><ul><li>10g 的干紫菜（喜欢紫菜的可以多放些）</li><li>两个鸡蛋</li><li>盐 2 克</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>干紫菜用清水泡 15 分钟，捞起沥干水份备用。</li><li>热锅，倒入 1.5 升清水、5ml 油、2g 盐。待水开后放入紫菜。</li><li>紫菜烧开后 3 分钟，将打好的蛋液徐徐倒入锅内，30 秒既可起锅。</li><li>撒上葱花，转小火 20 秒。</li><li>关火，出锅前放入几滴香油，也有的会放入一点虾皮，味道也不错。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>水开后，将火关小，将打好的蛋液围绕中间沸腾的水倒入。为了使蛋花比较嫩，锅盖盖上熄灭火等半分钟后再打开.</li><li>如果喜欢浓稠口感，可加入 2g 淀粉.</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const p=a(r,[["render",o]]);export{c as __pageData,p as default};
