import{_ as l,c as e,o as i,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/%E6%88%90%E5%93%81.BkQZQRfF.jpg",m=JSON.parse('{"title":"烙饼的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/staple/烙饼/烙饼.md","filePath":"dishes/staple/烙饼/烙饼.md"}'),s={name:"dishes/staple/烙饼/烙饼.md"};function o(n,a,h,d,c,_){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="烙饼的做法" tabindex="-1">烙饼的做法 <a class="header-anchor" href="#烙饼的做法" aria-label="Permalink to &quot;烙饼的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>油</li><li>面粉</li><li>电饼铛</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>注意：该主食不提供更少或更多的版本，这里选取了最适合新手的量：</p><ul><li>面粉 = 400g</li><li>热水 = 130ml（80 度）</li><li>冷水 = 130ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>将 400g 面粉倒入盆中，一半用凉水和面，一半用热水和面，搅拌成面絮，用手揉成团。用保鲜膜封起来，醒面 40 分钟</li><li>离醒面完成时间还有 10 分钟时，请查看<a href="./../../condiment/油酥.html">小技巧</a>中的油酥做法（热油酥效果更好）</li><li>醒好的面不用揉，稍微摁一下，用一横刀一竖刀将其分成四份。</li><li>搓圆，擀开，擀成与电饼铛大小差不多的饼，取 1/4 的油酥，将饼表面涂抹均匀</li><li>沿饼的半径切开，从外圈将其卷成圆锥形，然后将圆锥尾部捏好，防止油酥外漏。</li><li>按压面饼圆锥尖的地方，将其压扁，然后再次擀成与电饼铛大小差不多的面饼（厚度约为 3mm）</li><li>将电饼铛预热，涂上凉油（热锅凉油），将擀好的饼放入电饼铛中，将饼的上方也刷点油，涂抹均匀（锁住水分），盖上盖子</li><li>大火烙一分钟，打开盖子，将饼翻个面再烙一分钟</li><li>重复以上动作，完成饼的烙制</li></ul><p><img src="'+r+'" alt=""></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const p=l(s,[["render",o]]);export{m as __pageData,p as default};
