import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"凉拌黄瓜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/凉拌黄瓜.md","filePath":"dishes/vegetable_dish/凉拌黄瓜.md"}'),r={name:"dishes/vegetable_dish/凉拌黄瓜.md"};function s(o,l,h,d,n,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="凉拌黄瓜的做法" tabindex="-1">凉拌黄瓜的做法 <a class="header-anchor" href="#凉拌黄瓜的做法" aria-label="Permalink to &quot;凉拌黄瓜的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>黄瓜</li><li>醋</li><li>酱油</li><li>蒜</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每次制作前需要确定计划做几份。一份正好够 1 个人食用</p><p>总量：</p><ul><li>黄瓜 200 克 * 份数</li><li>醋 7.5 ml + 4 ml * 份数</li><li>酱油 5 ml + 2.5 ml * 份数</li><li>蒜 3 瓣 * 份数</li><li>盐 0.4 克 + 0.2 克 * 份数</li><li>香油 5 ml + 2 ml * 份数</li><li>蚝油 5 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>用菜刀将黄瓜拍扁，再剁成长 3 厘米的碎块</li><li>将碎黄瓜装入碗中</li><li>将蒜拍碎切成碎末</li><li>将醋，酱油，盐，蚝油和蒜依次倒入碗中搅拌均匀并腌制 15 分钟</li><li>将香油倒入碗中并均匀搅拌</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>部分情况下黄瓜端头有苦味，请洗净切下后确认</li><li>做好之后直接开吃，亦可先准备好后放入冰箱冷藏（并非冷冻）后食用</li><li>若放入冰箱冷藏，则需使用保鲜膜包紧，并在 8 小时内食用完毕，否则黄瓜会因过度腌制失去清脆口感</li><li>推荐黄瓜去皮，口感更好</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(r,[["render",s]]);export{c as __pageData,m as default};
