import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const u=JSON.parse('{"title":"地三鲜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/地三鲜.md","filePath":"dishes/vegetable_dish/地三鲜.md"}'),r={name:"dishes/vegetable_dish/地三鲜.md"};function s(o,l,h,d,n,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="地三鲜的做法" tabindex="-1">地三鲜的做法 <a class="header-anchor" href="#地三鲜的做法" aria-label="Permalink to &quot;地三鲜的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>茄子</li><li>土豆</li><li>尖椒</li><li>葱</li><li>姜</li><li>蒜</li><li>豆瓣酱</li><li>生抽</li><li>盐</li><li>糖</li><li>淀粉</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>注意，这道菜仅有足够 2-4 人食用的版本。</p><ul><li>茄子 100g</li><li>土豆 150g</li><li>尖椒 3 - 4 个</li><li>淀粉 20g</li></ul><p>使用上述条件，计算出计划使用的原材料比例。</p><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>土豆洗净、去皮。茄子、尖椒洗净。</li><li>葱 3g 切 0.5cm 小段。蒜 10g 剁碎。姜 10g 切沫。</li><li>茄子、土豆、尖椒均切成 15g 的小块。</li><li>热锅，加入 25ml 油。</li><li>加入土豆，煎炸大约 3 分钟，待其到大约 8 分熟，以显示金黄色为准。</li><li>捞出土豆，留下油。</li><li>加入茄子，煎炸大约 40 秒，待其到大约 7 分熟，以显示金黄色为准。</li><li>如果锅内已经没有流动的油，可以考虑补充 15ml 油。</li><li>放入葱 3g。姜 10g。</li><li>放入豆瓣酱 20ml。</li><li>放入生抽 10ml。</li><li>放入盐 8g。</li><li>放入糖 10g。</li><li>放入之前处理的土豆。</li><li>放入尖椒。</li><li>翻炒 1 分钟。</li><li>放入蒜 10g</li><li>加入 200ml 水和 20g 淀粉。</li><li>待水开后，汤减少一半时，关火盛盘。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const m=i(r,[["render",s]]);export{u as __pageData,m as default};
