import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const s="/cook/assets/1.DAd7YaTb.jpeg",o="/cook/assets/2.aVS5-QJh.jpeg",r="/cook/assets/3.C9jdvwVD.jpeg",_="/cook/assets/4.CaUJrbVY.jpeg",b=JSON.parse('{"title":"手撕包菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/手撕包菜/手撕包菜.md","filePath":"dishes/vegetable_dish/手撕包菜/手撕包菜.md"}'),c={name:"dishes/vegetable_dish/手撕包菜/手撕包菜.md"};function n(d,l,h,m,p,u){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="手撕包菜的做法" tabindex="-1">手撕包菜的做法 <a class="header-anchor" href="#手撕包菜的做法" aria-label="Permalink to &quot;手撕包菜的做法&quot;">​</a></h1><p>手撕包菜是一道色香味俱全的汉族名菜，属于湘菜系</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>包菜</li><li>五花肉</li><li>小米辣</li><li>食用油</li><li>料酒</li><li>生抽</li><li>香醋</li><li>鸡精</li><li>姜</li><li>蒜头</li><li>蒜苗</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>包菜 1 颗</li><li>五花肉 200 g</li><li>小米辣 2 根</li><li>食用油 60 ml</li><li>料酒 5 ml</li><li>生抽 5 ml</li><li>香醋 5 ml</li><li>鸡精 2 g</li><li>姜 2 片</li><li>蒜头 2 粒</li><li>蒜苗 0.5 根</li><li>盐 5 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>包菜对半切开，去掉中间白色部分【参见图一】</li><li>手撕包菜，碗中放入 2 g 盐，清洗包菜并沥干备用【参见图二】</li><li>姜片、蒜头、小米辣、蒜苗处理后备用【参见图三】</li><li>五花肉切片，清水清洗后备用</li><li>锅中加入 30 ml 食用油，倒入包菜翻炒，大火翻炒 1 分钟 后，加入 3 g 盐 ，继续翻炒 2 分钟 后取出备用</li><li>锅中加入 30 ml 食用油，倒入五花肉，大火翻炒 1 分钟</li><li>倒入姜片等材料，翻炒 1 分钟</li><li>倒入包菜翻炒后，加入 香醋、料酒、鸡精、料酒，大火继续翻炒，2 分钟 后出锅</li></ul><p><img src="'+s+'" alt="示例菜成品"><img src="'+o+'" alt="示例菜成品"><img src="'+r+'" alt="示例菜成品"><img src="'+_+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>步骤五中，翻炒时间需要实际情况做调整，一般是炒到包菜七分熟后即可。七分熟是指包菜已经出水质感变软</li><li>步骤五中，加盐的作用是锁住包菜水分的同时，给包菜上一点味道</li><li>最后一个步骤中，翻炒时间根据实际情况和个人口感做灵活调整</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',13)]))}const f=i(c,[["render",n]]);export{b as __pageData,f as default};
