import{_ as l,c as e,o as i,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/%E8%8F%A0%E8%8F%9C%E7%82%92%E9%B8%A1%E8%9B%8B.Dw4JLwTD.jpg",p=JSON.parse('{"title":"菠菜炒鸡蛋的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/菠菜炒鸡蛋/菠菜炒鸡蛋.md","filePath":"dishes/vegetable_dish/菠菜炒鸡蛋/菠菜炒鸡蛋.md"}'),s={name:"dishes/vegetable_dish/菠菜炒鸡蛋/菠菜炒鸡蛋.md"};function o(h,a,d,n,_,c){return i(),e("div",null,a[0]||(a[0]=[t('<h1 id="菠菜炒鸡蛋的做法" tabindex="-1">菠菜炒鸡蛋的做法 <a class="header-anchor" href="#菠菜炒鸡蛋的做法" aria-label="Permalink to &quot;菠菜炒鸡蛋的做法&quot;">​</a></h1><p>这道菜难度系数简单，营养丰富。</p><p><img src="'+r+'" alt="示例菜成品"></p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>菠菜</li><li>鸡蛋</li><li>食用油</li><li>食用盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 1 人的份量：</p><ul><li>菠菜 350g</li><li>鸡蛋 2 个</li><li>食用油 15ml</li><li>食用盐 5g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>菠菜去根，洗净，放在篮子里，焯水</li><li>将鸡蛋打入碗中，搅匀</li><li>热锅，加入 10ml 油</li><li>油热后，倒入鸡蛋液，中火翻炒 15 秒，先煎成蛋饼，然后再用锅铲切成小块</li><li>关火，将鸡蛋块 盛到盘子中，不要洗锅</li><li>重新开火，倒入 5ml 油，油热后，放入菠菜，大火 翻炒 15 秒后，倒入鸡蛋块，翻炒均匀</li><li>加入 5g 盐、100ml 饮用水，大火 翻炒 10 秒</li><li>关火，盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=l(s,[["render",o]]);export{p as __pageData,m as default};
