import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"蚝油生菜的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/蚝油生菜.md","filePath":"dishes/vegetable_dish/蚝油生菜.md"}'),r={name:"dishes/vegetable_dish/蚝油生菜.md"};function s(o,l,n,h,d,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="蚝油生菜的做法" tabindex="-1">蚝油生菜的做法 <a class="header-anchor" href="#蚝油生菜的做法" aria-label="Permalink to &quot;蚝油生菜的做法&quot;">​</a></h1><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>生菜</li><li>蚝油</li><li>大蒜</li><li>生抽</li><li>盐</li><li>白糖</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>这道菜分量和在川菜馆点的标准分量一样</p><ul><li>生菜 1 棵( 200 g ± 50 )</li><li>蚝油 6-8 ml</li><li>大蒜 4-5 瓣（做成蒜泥或切碎）</li><li>生抽 6 ml</li><li>盐 0.5 g</li><li>白糖 1 g</li><li>食用油 5-8 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>生菜洗净并去掉烂菜叶。</li><li>热锅，先放 1 L 清水（凉），然后在锅内放入 2 ml - 3 ml 食用油和 0.5 g 盐，等待锅中的水煮沸。</li><li>水沸后，放入生菜，将<strong>每一片</strong>生菜叶都焯水 10 s。</li><li>捞出生菜，控干水份，摆盘 。</li><li>调汁：将生抽 10 ml 、蚝油 6-8 ml 、盐 0.5 g 、 白糖 1 g 放入碗中调匀，并加入 10-15 ml 清水（凉）搅拌均匀。</li><li>再开火，热锅，放入食用油 5-8 ml，油热放入蒜泥。</li><li>等待有蒜香飘出，倒入调好的汁，煮沸即可，立马关火。</li><li>将锅中的汤汁均匀地<strong>浇</strong>在生菜上。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>这道菜富含维生素，做法简单，尝起来爽口又不上火。</p><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',11)]))}const m=i(r,[["render",s]]);export{c as __pageData,m as default};
