import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/1.CXh8Z8oG.jpeg",p=JSON.parse('{"title":"西红柿豆腐汤羹的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/西红柿豆腐汤羹/西红柿豆腐汤羹.md","filePath":"dishes/vegetable_dish/西红柿豆腐汤羹/西红柿豆腐汤羹.md"}'),s={name:"dishes/vegetable_dish/西红柿豆腐汤羹/西红柿豆腐汤羹.md"};function o(h,l,_,d,n,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="西红柿豆腐汤羹的做法" tabindex="-1">西红柿豆腐汤羹的做法 <a class="header-anchor" href="#西红柿豆腐汤羹的做法" aria-label="Permalink to &quot;西红柿豆腐汤羹的做法&quot;">​</a></h1><p>西红柿豆腐汤羹是一道很清淡美味的汤羹</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>西红柿</li><li>鸡蛋</li><li>豆腐</li><li>食用油</li><li>盐</li><li>淀粉</li><li>鸡精</li><li>香葱</li><li>姜</li><li>开水</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>西红柿 1 个</li><li>鸡蛋 1 个</li><li>豆腐 100 g</li><li>食用油 5 ml</li><li>盐 2 g</li><li>淀粉 5 g</li><li>鸡精 2 g</li><li>香葱 0.5 根</li><li>姜 1 片</li><li>开水 350 ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>西红柿切成小丁、鸡蛋打入碗中搅拌、豆腐切块备用</li><li>起锅烧油，放入姜片 5 S 后倒入入西红柿翻炒 30 S</li><li>锅中加入开水，汤水烧开，60 S 后到入鸡蛋液、豆腐块</li><li>汤水重新烧开后，加入水淀粉，沿一个方向搅拌 2 圈</li><li>加入鸡精、盐、香葱，30 S 后起锅</li></ul><p><img src="'+r+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(s,[["render",o]]);export{p as __pageData,m as default};
