import{_ as a,c as i,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/%E8%A5%BF%E8%91%AB%E8%8A%A6%E7%82%92%E9%B8%A1%E8%9B%8B.BguY61FW.jpeg",m=JSON.parse('{"title":"西葫芦炒鸡蛋的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/西葫芦炒鸡蛋/西葫芦炒鸡蛋.md","filePath":"dishes/vegetable_dish/西葫芦炒鸡蛋/西葫芦炒鸡蛋.md"}'),s={name:"dishes/vegetable_dish/西葫芦炒鸡蛋/西葫芦炒鸡蛋.md"};function o(n,l,h,d,_,c){return e(),i("div",null,l[0]||(l[0]=[t('<h1 id="西葫芦炒鸡蛋的做法" tabindex="-1">西葫芦炒鸡蛋的做法 <a class="header-anchor" href="#西葫芦炒鸡蛋的做法" aria-label="Permalink to &quot;西葫芦炒鸡蛋的做法&quot;">​</a></h1><p><img src="'+r+'" alt="西葫芦炒鸡蛋"></p><p>西葫芦炒鸡蛋是一道简单易做的家常菜。简单易购的食材，好吃又下饭。</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>西葫芦</li><li>鸡蛋</li><li>西红柿（可选）</li><li>食用盐</li><li>食用油</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>按照 2 人的份量：</p><ul><li>西葫芦 500g</li><li>西红柿 100g</li><li>鸡蛋 3 个</li><li>食用油 10-20ml</li><li>食用盐 6g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>西红柿洗净，切成小块，备用</li><li>西葫芦洗净，切成边长约为 4cm 的菱形，备用</li><li>打三个鸡蛋到碗里，打散搅匀，备用</li><li>热锅，锅内放入 5ml - 10ml 食用油</li><li>倒入鸡蛋，保持翻炒至鸡蛋成固体，用锅铲分成小块后盛到碗里，备用</li><li>锅内放入 5ml - 10ml 食用油，倒入西红柿，炒至变软</li><li>倒入西葫芦一起翻炒均匀，放入 6g 食用盐，将火调小然后<strong>等待 4 - 5 分钟</strong></li><li>倒入备用的鸡蛋，中火翻炒 15 秒</li><li>关火，盛盘</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const p=a(s,[["render",o]]);export{m as __pageData,p as default};
