import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const c=JSON.parse('{"title":"酸辣土豆丝的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/酸辣土豆丝.md","filePath":"dishes/vegetable_dish/酸辣土豆丝.md"}'),r={name:"dishes/vegetable_dish/酸辣土豆丝.md"};function s(o,l,h,d,n,_){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="酸辣土豆丝的做法" tabindex="-1">酸辣土豆丝的做法 <a class="header-anchor" href="#酸辣土豆丝的做法" aria-label="Permalink to &quot;酸辣土豆丝的做法&quot;">​</a></h1><p>酸辣土豆丝是一道简单易做的菜。色泽光亮，酸辣可口。辅料辣椒富含维生素 C。该菜用料简单，好学易做</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>土豆</li><li>大蒜</li><li>青椒</li><li>红椒</li><li>干辣椒</li><li>葱</li><li>生抽</li><li>陈醋</li><li>盐</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>土豆 240g（越细越长更好）</li><li>大蒜 4 瓣</li><li>青椒 0.5 个</li><li>红椒 0.5 个</li><li>干辣椒 3 个</li><li>葱 1 根</li><li>生抽 5ml</li><li>陈醋 10ml</li><li>盐 2g</li><li>食用油 10-15ml</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>土豆去皮、切丝（或用刨丝器）。</li><li>切好的土豆丝用清水清洗，去除多余的淀粉，然后对土豆丝焯水 10 秒。沥干，备用。</li><li>葱蒜干辣椒切小块，青红椒切丝。</li><li>热锅，小火热油爆香蒜和干辣椒。</li><li>加入青红椒翻炒几下，加入土豆丝翻炒至变色。</li><li>加 5ml 生抽，10ml 陈醋，蒜末，最后加入盐翻炒均匀即可。</li></ul><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><ul><li>清洗土豆丝淀粉一定要去干净，不然会全黏在一起</li><li>加入蒜末、盐后应尽快出锅，保留蒜香以及避免破坏口感。</li></ul><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(r,[["render",s]]);export{c as __pageData,m as default};
