import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.3ZWff6mY.js";const r="/cook/assets/1.He9QCPCO.jpeg",p=JSON.parse('{"title":"麻婆豆腐的做法","description":"","frontmatter":{},"headers":[],"relativePath":"dishes/vegetable_dish/麻婆豆腐/麻婆豆腐.md","filePath":"dishes/vegetable_dish/麻婆豆腐/麻婆豆腐.md"}'),s={name:"dishes/vegetable_dish/麻婆豆腐/麻婆豆腐.md"};function o(h,l,d,n,_,c){return e(),a("div",null,l[0]||(l[0]=[t('<h1 id="麻婆豆腐的做法" tabindex="-1">麻婆豆腐的做法 <a class="header-anchor" href="#麻婆豆腐的做法" aria-label="Permalink to &quot;麻婆豆腐的做法&quot;">​</a></h1><p>川菜经典，配饭一绝</p><h2 id="必备原料和工具" tabindex="-1">必备原料和工具 <a class="header-anchor" href="#必备原料和工具" aria-label="Permalink to &quot;必备原料和工具&quot;">​</a></h2><ul><li>嫩豆腐</li><li>猪肉</li><li>牛肉 (可选，替代猪肉）</li><li>食用油</li><li>淀粉</li><li>香葱</li><li>姜末</li><li>豆瓣酱</li><li>食用盐</li><li>藤椒油/花椒油 （可选）</li></ul><h2 id="计算" tabindex="-1">计算 <a class="header-anchor" href="#计算" aria-label="Permalink to &quot;计算&quot;">​</a></h2><p>每份：</p><ul><li>嫩豆腐 100 g</li><li>猪肉 50 g</li><li>食用油 10 ml</li><li>淀粉 <ul><li>10g 偏稀</li><li>15g 正常</li><li>20g 较多</li></ul></li><li>香葱 0.5 根</li><li>姜末 1 粒</li><li>豆瓣酱 20 g</li><li>食用盐 2 g</li><li>藤椒油/花椒油 3 g</li></ul><h2 id="操作" tabindex="-1">操作 <a class="header-anchor" href="#操作" aria-label="Permalink to &quot;操作&quot;">​</a></h2><ul><li>豆腐切块（每块 2 * 2 cm ），猪肉/牛肉切成肉丁 （或者切细成肉末）</li><li>起锅烧油，倒入蒜末、豆瓣酱翻炒 5 S 后，倒入肉丁/肉末继续翻炒 30 S</li><li>加入开水，水煮 30 S 后，倒入 豆腐，继续煮 2-3 分钟</li><li>中火烧干汁，加入水淀粉、食用盐后起锅 （也可以在起锅前浇上藤椒油增香增麻）</li><li>散上葱花</li></ul><p><img src="'+r+'" alt="示例菜成品"></p><h2 id="附加内容" tabindex="-1">附加内容 <a class="header-anchor" href="#附加内容" aria-label="Permalink to &quot;附加内容&quot;">​</a></h2><p>如果您遵循本指南的制作流程而发现有问题或可以改进的流程，请提出 Issue 或 Pull request 。</p>',12)]))}const m=i(s,[["render",o]]);export{p as __pageData,m as default};
